mapper.select:
       select d.*,c.name categoryName from dish d left join
           category c on d.category_id = c.id  
       
           and name like concat('%',#{name},'%')
       
       
           and category_id=#{categoryId}
       
       
           and d.status=#{status}
       
   
order by create_time desc

        select * from dish 
         and name like concat('%',#{name},'%')
